export type InputControlType = 'text' | 'email' | 'number' | 'date' | 'time' | 'tel' | 'color' | 'url' | 'search';

export interface SelectControlOption {
  value: any,
  text?: string,
  selected?: boolean | ((value: any) => boolean),
  disabled?: boolean | ((value: any) => boolean)
}

